import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import fetchTokenTrivia from '../services';
import { emailAction, usernameAction } from '../redux/actions';
import triviaImg from '../images/trivia_game.png';

class Login extends Component {
  constructor() {
    super();
    this.state = {
      userName: '',
      email: '',
    };
    this.handleChange = this.handleChange.bind(this);
    this.configPagePush = this.configPagePush.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  configPagePush = () => {
    const { history } = this.props;
    history.push('/configuracao');
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  async handleClick() {
    const token = await fetchTokenTrivia();
    localStorage.setItem('token', token);
    const { history, emailDispatch, usernameDispatch } = this.props;
    const { email, userName } = this.state;
    history.push('/game');
    emailDispatch(email);
    usernameDispatch(userName);
  }

  render() {
    const { userName, email } = this.state;
    return (
      <div
        className="login container rounded d-flex flex-column p-3"
      >
        <img
          src={ triviaImg }
          alt="trivia logo"
        />
        <form
          action=""
        >
          <div
            className="d-flex flex-column align-items-center mb-3"
          >
            <input
              type="text"
              name="userName"
              id="userName"
              data-testid="input-player-name"
              onChange={ this.handleChange }
              value={ userName }
              className="form-control"
              placeholder="username"
            />
            <br />
            <input
              type="email"
              name="email"
              id="email"
              data-testid="input-gravatar-email"
              onChange={ this.handleChange }
              value={ email }
              className="form-control"
              placeholder="email"
            />
          </div>
          <div
            className="input-group d-flex justify-content-center"
          >
            <button
              disabled={ !((userName.length > 0 && email.length > 0)) }
              data-testid="btn-play"
              type="button"
              onClick={ this.handleClick }
              className="btn btn-light"
            >
              Play
            </button>
            <button
              data-testid="btn-settings"
              type="button"
              onClick={ this.configPagePush }
              className="btn btn-light"
            >
              Configurações
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  emailDispatch: (payload) => dispatch(emailAction(payload)),
  usernameDispatch: (payload) => dispatch(usernameAction(payload)),
});

Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
  emailDispatch: PropTypes.func.isRequired,
  usernameDispatch: PropTypes.func.isRequired,
};

export default connect(null, mapDispatchToProps)(Login);
